hexToBase64 :: T.Text -> T.Text
hexToBase64 t = let
  sstr = fromMaybe t $ T.stripPrefix "0x" t
  t16 = either (error "decoding failed") id $ B16.decode $ T.encodeUtf8 sstr
  in encodeB64UrlNoPaddingText t16
